import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Image } from 'react-native';
import Input from '../components/Input';
import Card from '../components/Card';
import Botton from '../components/Botton';
import useProfile from '../hooks/useProfile';
import FlashMessage, { showMessage } from 'react-native-flash-message';
import { useValidarUsuario } from '../hooks/useValidacion';

export default function LoginScreen({ navigation }) {
  const { profile } = useProfile();
  const [usuario, setUsuario, ErrorUsuario, validarUsuario, resetUsuario] = useValidarUsuario();

  const handleUsuarioChange = (text) => {
    setUsuario(text);
    validarUsuario();  
    if (usuario && !ErrorUsuario) {
      resetUsuario();
    }
  };

  const handleLogin = () => {
    validarUsuario();
    if (usuario && !ErrorUsuario) {
      if (usuario == profile.cedula) {
        navigation.navigate('Home');
        setUsuario(''); 
        resetUsuario();
      } else {
        showMessage({
          message: 'Error de Usuario o Contraseña!',
          description: 'Ingrese los Datos Correctamente.',
          type: 'danger',
        });
      }
    } else {
      showMessage({
        message: 'Error de Datos!',
        description: 'Ingrese los Datos Correctamente.',
        type: 'danger',
      });
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Image source={require('../assets/logo.png')} style={styles.icon} />
        <Text style={styles.welcomeText}>Chiwi Chat</Text>
      </View>

      <Text style={styles.subtitle}>¡Bienvenido a Chiwichat, la mejor forma de chatear!</Text>
      <Input label="Usuario" icon='user' placeholder='Ingresar el Usuario' value={usuario} onChangeText={handleUsuarioChange}  error={ErrorUsuario} iconError={ErrorUsuario ? 'warning' : null}/>

      <Botton title="Iniciar Sesión" onPress={handleLogin}/>

      <TouchableOpacity style={styles.forgotPassword} onPress={() => navigation.navigate('RecuperarContraseña')}>
        <Text style={styles.forgotPasswordText}>Abrir Cuenta Nueva</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    padding: '10%',
  },
  header: {
    flexDirection: 'row',  // Aligns items horizontally
    alignItems: 'center',  // Centers items vertically
    marginBottom: 20,
  },
  icon: {
    width: 100,
    height: 70,
    marginRight: 10,
  },
  welcomeText: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#0066CC',
  },
  subtitle: {
    fontSize: 16,
    color: '#808080',
    marginBottom: 20,
    textAlign: 'center',
  },
  forgotPassword: {
    marginTop: 10,
  },
  forgotPasswordText: {
    color: '#0066CC',
    fontSize: 14,
    textAlign: 'center',
    marginBottom: 10,
  },
});
